mapper.select:
/* [mms.member.getMemberDepositList][allen][2016. 7. 19.] */
SELECT T1.MEMBER_NO , T1.DEPOSIT_NO , T1.DEPOSIT_AMT , T1.DEPOSIT_TYPE_CD , T1.NOTE , T1.DEPOSIT_AMT + (SELECT NVL(SUM (DEPOSIT_AMT), 0) FROM MMS_DEPOSIT WHERE DEPOSIT_NO < T1.DEPOSIT_NO AND MEMBER_NO = T1.MEMBER_NO) AS BALANCE_AMT , T1.ORDER_ID , T1.INS_DT , T1.INS_ID , (SELECT NAME FROM CCS_USER WHERE USER_ID = T1.INS_ID) INS_NAME , T1.UPD_DT , T1.UPD_ID , (SELECT NAME FROM CCS_USER WHERE USER_ID = T1.UPD_ID) UPD_NAME , (SELECT DECODE(SUM(DEPOSIT_AMT), null, 0, SUM(DEPOSIT_AMT)) FROM MMS_DEPOSIT WHERE MEMBER_NO = #{memberNo} AND SIGN(DEPOSIT_AMT) = 1
AND INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND DEPOSIT_TYPE_CD IN (${depositTypeCds})
) AS depositPlusAmt , (SELECT DECODE(SUM(DEPOSIT_AMT), null, 0, SUM(DEPOSIT_AMT)) FROM MMS_DEPOSIT WHERE MEMBER_NO = #{memberNo} AND SIGN(DEPOSIT_AMT) = -1
AND INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND DEPOSIT_TYPE_CD IN (${depositTypeCds})
) AS depositMinusAmt FROM MMS_DEPOSIT T1 WHERE T1.MEMBER_NO = #{memberNo}
AND T1.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND T1.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND T1.DEPOSIT_TYPE_CD IN (${depositTypeCds})
ORDER BY T1.INS_DT DESC

/* [mms.member.getDepositList][ian][2016. 8. 17.] */
SELECT MD.DEPOSIT_NO, MD.MEMBER_NO, MM.MEMBER_ID, MM.MEMBER_NAME, MD.DEPOSIT_TYPE_CD, MD.DEPOSIT_AMT,
MD.DEPOSIT_AMT + (SELECT NVL(SUM (DEPOSIT_AMT), 0) FROM MMS_DEPOSIT WHERE DEPOSIT_NO < MD.DEPOSIT_NO AND MEMBER_NO = MD.MEMBER_NO) AS BALANCE_AMT, MD.INS_DT, MD.NOTE, OO.ORDER_ID, OC.CLAIM_NO, MD.UPD_ID, MD.UPD_DT, (SELECT US.NAME FROM CCS_USER US WHERE MD.UPD_ID = US.USER_ID AND ROWNUM=1) AS UPD_NAME FROM MMS_DEPOSIT MD INNER JOIN MMS_MEMBER MM ON MM.MEMBER_NO = MD.MEMBER_NO INNER JOIN MMS_MEMBER_ZTS MMZ ON MMZ.MEMBER_NO = MM.MEMBER_NO LEFT OUTER JOIN OMS_ORDER OO ON OO.ORDER_ID = MD.ORDER_ID LEFT OUTER JOIN OMS_CLAIM OC ON OC.ORDER_ID = OO.ORDER_ID WHERE 1=1
AND MD.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MD.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND MD.DEPOSIT_TYPE_CD IN (${depositTypeCds})
AND MM.MEMBER_NAME = #{searchKeyword}
AND MM.MEMBER_ID = #{searchKeyword}
AND OO.ORDER_ID = #{orderId}
AND OC.CLAIM_NO = #{claimNo}
ORDER BY INS_DT DESC, DEPOSIT_NO DESC

/* [mms.member.getMemberCarrotList][allen][2016. 7. 19.] */
SELECT MC.MEMBER_NO , MC.CARROT_NO , MC.CARROT , MC.CARROT_TYPE_CD , MC.NOTE , MC.CARROT + (SELECT NVL(SUM (CARROT), 0) FROM MMS_CARROT WHERE CARROT_NO < MC.CARROT_NO AND MEMBER_NO = MC.MEMBER_NO) AS BALANCE_AMT , MC.EXPIRE_DT , MC.INS_DT , MC.INS_ID , (SELECT NAME FROM CCS_USER WHERE USER_ID = MC.INS_ID) INS_NAME , MC.UPD_ID , MC.UPD_DT , (SELECT NAME FROM CCS_USER WHERE USER_ID = MC.UPD_ID) UPD_NAME , (SELECT DECODE(SUM(CARROT), null, 0, SUM(CARROT)) FROM MMS_CARROT WHERE MEMBER_NO = #{memberNo} AND SIGN(CARROT) = 1 AND CARROT_TYPE_CD = 'CARROT_TYPE_CD.EVENT'
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
) AS eventPlusAmt , (SELECT DECODE(SUM(CARROT), null, 0, SUM(CARROT)) FROM MMS_CARROT WHERE MEMBER_NO = #{memberNo} AND SIGN(CARROT) = -1 AND CARROT_TYPE_CD = 'CARROT_TYPE_CD.EVENT'
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
) AS eventMinusAmt , (SELECT DECODE(SUM(CARROT), null, 0, SUM(CARROT)) FROM MMS_CARROT WHERE MEMBER_NO = #{memberNo} AND SIGN(CARROT) = 1 AND CARROT_TYPE_CD = 'CARROT_TYPE_CD.CS'
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
) AS csPlusAmt , (SELECT DECODE(SUM(CARROT), null, 0, SUM(CARROT)) FROM MMS_CARROT WHERE MEMBER_NO = #{memberNo} AND SIGN(CARROT) = -1 AND CARROT_TYPE_CD = 'CARROT_TYPE_CD.CS'
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
) AS csMinusAmt , (SELECT DECODE(SUM(BALANCE_CARROT), null, 0, SUM(BALANCE_CARROT)) FROM MMS_CARROT WHERE MEMBER_NO = #{memberNo} AND SIGN(CARROT) = 1 AND EXPIRE_DT <= SYSDATE
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
) AS expiredAmt FROM MMS_CARROT MC WHERE MC.MEMBER_NO = #{memberNo}
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND MC.CARROT_TYPE_CD IN (${carrotTypeCds})
ORDER BY INS_DT DESC, CARROT_NO DESC

/* [mms.member.getCarrotList][allen][2016. 7. 19.] */
SELECT MC.MEMBER_NO, MC.CARROT_NO, MC.CARROT, MC.CARROT_TYPE_CD, MC.NOTE, MC.CARROT + (SELECT NVL(SUM (CARROT), 0) FROM MMS_CARROT WHERE CARROT_NO < MC.CARROT_NO AND MEMBER_NO = MC.MEMBER_NO) AS BALANCE_AMT, MC.EXPIRE_DT, MC.INS_DT, MC.INS_ID, MC.UPD_DT, MC.UPD_ID, MM.MEMBER_ID, MM.MEMBER_NAME, (SELECT US.NAME FROM CCS_USER US WHERE MC.INS_ID = US.USER_ID AND ROWNUM=1) AS INS_NAME, (SELECT US.NAME FROM CCS_USER US WHERE MC.UPD_ID = US.USER_ID AND ROWNUM=1) AS UPD_NAME FROM MMS_CARROT MC INNER JOIN MMS_MEMBER_ZTS MMZ ON MMZ.MEMBER_NO = MC.MEMBER_NO INNER JOIN MMS_MEMBER MM ON MM.MEMBER_NO = MMZ.MEMBER_NO WHERE 1=1
AND MM.MEMBER_NAME = #{searchKeyword}
AND MM.MEMBER_ID = #{searchKeyword}
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND MC.MEMBER_NO = #{memberNo}
AND MC.CARROT_TYPE_CD IN (${carrotTypeCds})
AND MC.NOTE LIKE '%' || #{note} || '%'
AND MMZ.MEM_GRADE_CD IN (${memGradeCds})
ORDER BY INS_DT DESC, CARROT_NO DESC

/* [mms.member.getCarrotSummery][ian][2016. 8. 05.] */ SELECT MC.CARROT_TYPE_CD, NVL (T1.MEM_CNT, 0) AS PLUS_MEM, NVL (T1.SUM, 0) AS PLUS_CARROT, NVL (T2.MEM_CNT, 0) AS MINUS_MEM, NVL (T2.SUM, 0) AS MINUS_CARROT FROM (SELECT DISTINCT CARROT_TYPE_CD FROM MMS_CARROT) MC LEFT OUTER JOIN ( SELECT A1.CARROT_TYPE_CD, SUM (A1.MEM_CNT) AS MEM_CNT, SUM (A1.SUM) AS SUM FROM ( SELECT MC.CARROT_TYPE_CD, COUNT (DISTINCT MC.MEMBER_NO) AS MEM_CNT, SUM (MC.CARROT) AS SUM FROM MMS_CARROT MC INNER JOIN MMS_MEMBER_ZTS MMZ ON MMZ.MEMBER_NO = MC.MEMBER_NO INNER JOIN MMS_MEMBER MM ON MM.MEMBER_NO = MMZ.MEMBER_NO WHERE MC.CARROT > 0
AND MC.MEMBER_NO = #{memberNo}
AND MM.MEMBER_NAME = #{searchKeyword}
AND MM.MEMBER_ID = #{searchKeyword}
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND MC.CARROT_TYPE_CD IN (${carrotTypeCds})
AND MC.NOTE LIKE '%' || #{note} || '%'
AND MMZ.MEM_GRADE_CD IN (${memGradeCds})
GROUP BY MC.CARROT_TYPE_CD, MC.MEMBER_NO) A1 GROUP BY A1.CARROT_TYPE_CD) T1 ON T1.CARROT_TYPE_CD = MC.CARROT_TYPE_CD LEFT OUTER JOIN ( SELECT A1.CARROT_TYPE_CD, SUM (A1.MEM_CNT) AS MEM_CNT, SUM (A1.SUM) AS SUM FROM ( SELECT MC.CARROT_TYPE_CD, COUNT (DISTINCT MC.MEMBER_NO) AS MEM_CNT, SUM (MC.CARROT) AS SUM FROM MMS_CARROT MC INNER JOIN MMS_MEMBER_ZTS MMZ ON MMZ.MEMBER_NO = MC.MEMBER_NO INNER JOIN MMS_MEMBER MM ON MM.MEMBER_NO = MMZ.MEMBER_NO WHERE MC.CARROT < 0
AND MC.MEMBER_NO = #{memberNo}
AND MM.MEMBER_NAME = #{searchKeyword}
AND MM.MEMBER_ID = #{searchKeyword}
AND MC.INS_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND MC.INS_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
AND MC.CARROT_TYPE_CD IN (${carrotTypeCds})
AND MC.NOTE LIKE '%' || #{note} || '%'
AND MMZ.MEM_GRADE_CD IN (${memGradeCds})
GROUP BY MC.CARROT_TYPE_CD, MC.MEMBER_NO) A1 GROUP BY A1.CARROT_TYPE_CD) T2 ON T2.CARROT_TYPE_CD = MC.CARROT_TYPE_CD WHERE MC.CARROT_TYPE_CD IS NOT NULL ORDER BY MC.CARROT_TYPE_CD ASC

