SELECT TO_CHAR((SELECT SUM(ORDER_AMT) AS TOT_CNT FROM OMS_POSORDER WHERE MEMBER_NO = #{memberNo}), 'FM9,999,999') AS TOTALAMT , (SELECT COUNT(*) AS TOT_CNT FROM OMS_POSORDER WHERE MEMBER_NO = #{memberNo}) AS TOTALCOUNT , T2.AREA_DIV1 , T2.NAME FROM OMS_POSORDER T1 JOIN CCS_OFFSHOP T2 ON T1.OFFSHOP_ID = T2.OFFSHOP_ID WHERE MEMBER_NO IN (#{memberNo}, (select offshop_member_no from mms_member where member_no=#{memberNo}))
AND T2.AREA_DIV1 = #{areaDiv1}
GROUP BY T2.AREA_DIV1, T2.NAME

SELECT T1.ORDER_ID , T1.MEMBER_NO , T1.CARD_ISSUE_NO , T1.OFFSHOP_ID , T1.PRODUCT_AMT , T1.TAX , T1.ORDER_AMT , T1.ORDER_DT , T1.USE_POINT , T1.SAVE_POINT , T1.INS_DT , T1.INS_ID , T1.UPD_DT , T1.UPD_ID , T1.AVAIL_POINT , T2.ERP_SALEPRODUCT_ID , T2.TAX_TYPE , T2.PRODUCT_NAME , T2.SALE_PRICE , T2.ORDER_QTY
, T2.BRAND_NAME , T3.NAME , T3.ADDRESS1 , T3.MANAGER_PHONE , T3.OFFSHOP_PHONE
FROM OMS_POSORDER T1 JOIN OMS_POSORDERPRODUCT T2 ON T1.ORDER_ID = T2.ORDER_ID JOIN CCS_OFFSHOP T3 ON T1.OFFSHOP_ID = T3.OFFSHOP_ID
WHERE T1.MEMBER_NO IN (#{memberNo}, (select offshop_member_no from mms_member where member_no=#{memberNo}))
AND T1.ORDER_ID = #{orderId}
AND T3.AREA_DIV1 = #{areaDiv1}
AND T3.NAME = #{branchName}
AND T1.ORDER_DT >= TO_DATE (#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND T1.ORDER_DT <= TO_DATE (#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
ORDER BY T2.ERP_SALEPRODUCT_ID

